    markup.add("üé• Video yuklash", "üì© Admin bilan aloqa", "üé® Rasm yasash")
    bot.send_message(
        message.chat.id,
        "Assalomu alaykum!\nPastdagi tugmalardan birini tanlang:",
        reply_markup=markup
    )

